function checkSproutLinkField(id) {

	// Grab our input field ID
	var sproutLinkFieldId = '#' + id;
	var sproutLinkButtonClass = '.' + id;

	// We use setTimeout to make sure our function works every time
	setTimeout(function() 
	{
		// URL Pattern
		var urlPattern = /^(http|ftp|https):\/\/[\w-]+(\.[\w-]+)+([\w.,@?^=%&amp;:\/~+#-]*[\w@?^=%&amp;\/~+#-])?/;

		// Get the value of our input field
		var url = $(sproutLinkFieldId).val();

		// If the input field matches our URL Pattern
		// display a link to the URL. If not, make the
		// link to the URL disappear
		if ( urlPattern.test(url) )
		{
			$(sproutLinkButtonClass).addClass('fade');
			$(sproutLinkButtonClass).html('<a href="' + url + '" target="_blank">Visit URL &rarr;</a>');
		}
		else
		{
			$(sproutLinkButtonClass).removeClass('fade');
		}

	}, 500);
}